function getAttributeCategoryName($i)
{
	if ($i > 6)
		return "getAttributeCategoryName : Wrong attribute type (" . $i . ")";
	else if ($i == 0)
		return "Attributes";
	else if ($i == 1)
		return "AttributesEx";
	else
		return "AttributesEx" . $i;
}

function getAttributeName($a,$i)
{
	global $Attributes,$AttributesEx,$AttributesEx2,$AttributesEx3,$AttributesEx4,$AttributesEx5,$AttributesEx6;
	if ($i > 32)
	    return "Wrong index (" . $i . ")";
		
	switch($a)
	{
	case 0:
		return $Attributes[$i];
	case 1:
		return $AttributesEx[$i];
	case 2:
		return $AttributesEx2[$i];
	case 3:
		return $AttributesEx3[$i];
	case 4:
		return $AttributesEx4[$i];
	case 5:
		return $AttributesEx5[$i];
	case 6:
		return $AttributesEx6[$i];
	default:
		return "Wrong attribute type (" . $i . ")";
	}
}
